buildGradleAdditions.insert:
apply plugin: 'com.google.gms.google-services'

android 
{
    buildTypes 
    {
        buildTypes.each
        {
            it.signingConfig signingConfigs.release
            it.resValue "string", "FacebookAppID", FACEBOOK_APP_ID
        }
    }
}

gameActivityImportAdditions.insert:
import com.thegetaway.firebaseauthentication.EmailPasswordAuthentication;
import com.thegetaway.firebaseauthentication.AnonymousAuthentication;
import com.thegetaway.firebaseauthentication.FacebookAuthentication;
import com.thegetaway.firebaseauthentication.GoogleAuthentication;
import com.thegetaway.firebaseauthentication.OAuthAuthentication;
import com.thegetaway.firebaseauthentication.PhoneAuthentication;
import com.thegetaway.firebaseauthentication.BaseAuthentication;

gameActivityOnCreateAdditions.insert:
// Initialize classes
EmailPasswordAuth = new EmailPasswordAuthentication();
AnonymousAuth = new AnonymousAuthentication();
FacebookAuth = new FacebookAuthentication(this);
GoogleAuth = new GoogleAuthentication(this);
PhoneAuth = new PhoneAuthentication(this);
OAuth = new OAuthAuthentication(this);

gameActivityClassAdditions.insert:
private EmailPasswordAuthentication EmailPasswordAuth;
private AnonymousAuthentication AnonymousAuth;
private FacebookAuthentication FacebookAuth;
private GoogleAuthentication GoogleAuth;
private PhoneAuthentication PhoneAuth;
private OAuthAuthentication OAuth;

// Email/Password Authentication methods
private void AndroidThunkJava_EmailPasswordSignIn(String Email, String Password) { EmailPasswordAuth.EmailPasswordSignIn(Email, Password); }
private void AndroidThunkJava_CreateAccount(String Email, String Password) { EmailPasswordAuth.CreateAccount(Email, Password); }
private void AndroidThunkJava_SendEmailVerification() { EmailPasswordAuth.SendEmailVerification(); }

// PhoneAuthentication Authentication methods
private void AndroidThunkJava_StartPhoneNumberVerification(String PhoneNumber, int Timeout) { PhoneAuth.StartPhoneNumberVerification(PhoneNumber, Timeout); }
private void AndroidThunkJava_ResendVerificationCode(String PhoneNumber, int Timeout) { PhoneAuth.ResendVerificationCode(PhoneNumber, Timeout); }
private void AndroidThunkJava_VerifyPhoneNumberWithCode(String Code) { PhoneAuth.VerifyPhoneNumberWithCode(Code); }

// Google Authentication methods
private void AndroidThunkJava_GoogleSignIn() { GoogleAuth.GoogleSignIn(); }
private void AndroidThunkJava_GoogleSignOut() { GoogleAuth.GoogleSignOut(); }
private void AndroidThunkJava_GoogleRevokeAccess() { GoogleAuth.GoogleRevokeAccess(); }

// Anonymous Authentication methods
private void AndroidThunkJava_AnonymousSignIn() { AnonymousAuth.AnonymousSignIn(); }
private void AndroidThunkJava_AnonymousLinkAccount(String Email, String Password) { AnonymousAuth.AnonymousLinkAccount(Email, Password); }

// Facebook Authentication methods
private void AndroidThunkJava_FacebookSignIn() { FacebookAuth.FacebookSignIn(); }
private void AndroidThunkJava_FacebookSignOut() { FacebookAuth.FacebookSignOut(); }

// OAuth Authentication methods
private void AndroidThunkJava_OAuthSignIn(String ProviderID) { OAuth.OAuthSignIn(ProviderID); }

// Firebase methods
private void AndroidThunkJava_FirebaseSignOut() { BaseAuthentication.FirebaseAccountSignOut(); }
